```swift
import Foundation

class Edge {
    let to: Int
    var weight: Int
    init(to: Int, weight: Int) {
        self.to = to
        self.weight = weight
    }
}

class Graph {
    var adjacencyList: [[Edge]]

    init(vertexCount: Int) {
        self.adjacencyList = Array(repeating: [], count: vertexCount)
    }

    func addEdge(from u: Int, to v: Int, weight: Int) {
        adjacencyList[u].append(Edge(to: v, weight: weight))
        adjacencyList[v].append(Edge(to: u, weight: weight))
    }

    func minimumCut() -> Int {
        var minCut = Int.max
        var vertices = Array(0..<adjacencyList.count)
        var weights = Array(repeating: 0, count: adjacencyList.count)
        var exist = Array(repeating: true, count: adjacencyList.count)
        var last = 0

        for _ in 0..<adjacencyList.count-1 {
            weights = Array(repeating: 0, count: adjacencyList.count)
            var used = Array(repeating: false, count: adjacencyList.count)
            var prev = 0

            for _ in 1..<vertices.count {
                var maxWeight = -1
                var maxVertex = -1

                for j in vertices where exist[j] && !used[j] {
                    if weights[j] > maxWeight {
                        maxWeight = weights[j]
                        maxVertex = j
                    }
                }

                if maxVertex == -1 {
                    return minCut
                }

                used[maxVertex] = true
                last = maxVertex
                prev = maxVertex

                for edge in adjacencyList[maxVertex] {
                    if exist[edge.to] {
                        weights[edge.to] += edge.weight
                    }
                }
            }

            minCut = min(minCut, weights[last])

            // Merge vertices
            exist[prev] = false
            for i in 0..<adjacencyList[prev].count {
                let edge = adjacencyList[prev][i]
                if exist[edge.to] {
                    adjacencyList[last].append(Edge(to: edge.to, weight: edge.weight))
                    adjacencyList[edge.to].append(Edge(to: last, weight: edge.weight))
                }
            }

            // Remove self loops
            adjacencyList[last].removeAll { edge in
                !exist[edge.to] || edge.to == last
            }
        }
        return minCut
    }
}

