       public CreateProductRequestValidator()
       {
           RuleFor(x => x.Name).NotEmpty();
           RuleFor(x => x.Price).GreaterThan(0);
           // Add more validation rules as needed
       }
   }
   ```

3. Configure Fluent Validation and Swashbuckle in your Startup.cs file:
   ```csharp
   public void ConfigureServices(IServiceCollection services)
   {
       // Add Fluent Validation
       services.AddControllers().AddFluentValidation(fv => fv.RegisterValidatorsFromAssemblyContaining<Startup>());

       // Add Swagger
       services.AddSwaggerGen(c =>
       {
           c.SwaggerDoc("v1", new OpenApiInfo { Title = "Your API", Version = "v1" });
           c.AddFluentValidationRules();
       });
   }

   public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
   {
       // Enable Swagger
       app.UseSwagger();
       app.UseSwaggerUI(c =>
       {
           c.SwaggerEndpoint("/swagger/v1/swagger.json", "Your API V1");
           c.RoutePrefix = string.Empty;
       });
   }
   ```

4. Apply the Fluent Validation rules to your API controllers. For example:
   ```csharp
   [ApiController]
   [Route("api/[controller]")]
   public class ProductsController : ControllerBase
   {
       private readonly IProductService _productService;

       public ProductsController(IProductService productService)
       {
           _productService = productService;
       }

       [HttpPost]
       public IActionResult CreateProduct([FromBody] CreateProductRequest request)
       {
           var validationResult = new CreateProductRequestValidator().Validate(request);
           if (!validationResult.IsValid)
           {
               return BadRequest(validationResult.Errors);
           }

           // Call the service to create the product
           _productService.CreateProduct(request);

           return Ok();
       }
   }
   ```

