    ret = clGetProgramInfo(program, CL_PROGRAM_BINARY_SIZES, sizeof(size_t), &binary_size, NULL);
    unsigned char *binary = (unsigned char *)malloc(binary_size);
    ret = clGetProgramInfo(program, CL_PROGRAM_BINARIES, binary_size, &binary, NULL);

    \/\/ Save the precompiled binary to a file
    fp = fopen("kernel.bin", "wb");
    if (!fp) {
        perror("Error opening binary file");
        return 1;
    }
    fwrite(binary, binary_size, 1, fp);
    fclose(fp);

    \/\/ Clean up
    free(kernel_source);
    free(binary);
    clFlush(queue);
    clFinish(queue);
    clReleaseCommandQueue(queue);
    clReleaseContext(context);
    clReleaseProgram(program);

    return 0;
}
