func stoerWagnerMinCut(weights: [[Int]]) -> Int {

    let n = weights.count

    var graph = weights
    var minCut = Int.max

    for _ in 0..<n-1 {

        var contracted: [Int] = Array(repeating: 0, count: n)
        var group = Set<Int>()

        var cut = 0
        var prev = 0

        for i in 0..<n {

