            \/\/ Compute min cut for vertex v
            var w = -1
            for j in 0..<n {
                if contracted[j] == 0 && j != v {
                    let currCut = graph[v][j]
                    if currCut > cut {
                        cut = currCut
                        w = j
                    }
                }
            }

            \/\/ Merge v and w vertices
            contracted[v] = 1
            if w >= 0 {
                contracted[w] = 1
                group.insert(v)
                group.insert(w)

                for k in 0..<n {
                    graph[prev][k] += graph[v][k] + graph[w][k]
                    graph[k][prev] = graph[prev][k] 
                }
            }
            prev = v
        }

        minCut = min(minCut, cut)

        \/\/ Contract group into single vertex
        let u = n
        for i in 0..<n {
            if !group.contains(i) {
                graph[i][u] = graph[u][i] = 0
            }
        }
        for v in group {
            for i in 0..<n {
                graph[prev][i] += graph[v][i]
                graph[i][prev] += graph[v][i]
            }
        }
    }

    return minCut
