                if (!dfs(i, visited, stack)) {
                    return false; \/\/ Cycle detected
                }
            }
        }

        \/\/ If there was no cycle, the topological ordering is in the stack
        while (!stack.empty()) {
            result.push_back(stack.top());
            stack.pop();
        }

        return true; \/\/ No cycles, topological sort successful
